<?php


namespace App\Services;


use App\Exceptions\CustomException;
use App\Interfaces\UserInterface;
use App\Models\User;
use Illuminate\Http\Response;
use Illuminate\Pagination\LengthAwarePaginator;
use Illuminate\Support\Facades\Hash;

class UserService implements UserInterface
{


    public function index() : LengthAwarePaginator
    {
        return User::with(['roles','permissions'])->paginate(config('util.paginate'));
    }

    public function create(array $data): User
    {
        $img = [
            'https://engineering.unl.edu/images/staff/Kayla-Person.jpg',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRukY7latzWBcyjOCAHRY2cPt7Yq-Mhh2CX4K08vPebqQcfANxGNxxHm553M-MxMaeP6sU&usqp=CAU',
            'https://cdn.psychologytoday.com/sites/default/files/styles/article-inline-half-caption/public/field_blog_entry_images/2018-09/shutterstock_648907024.jpg?itok=0hb44OrI'
        ];
        $user = User::create([
            "employee_id" => $data['employee_id'],
            "first_name" => $data['first_name'],
            "last_name" => $data['last_name'],
            "email" => $data['email'],
            "username" => $data['username'],
            'img_url' => $img[array_rand($img,1)],
            "mobile_number" => $data['mobile_number'],
            "password" => Hash::make($data['password']),
        ]);

        if(!$user) {
            throw new CustomException("Could not create user", Response::HTTP_BAD_REQUEST);
        }

        $user->assignRole($data['role']);

        $permissions =  data_get($data,'permissions');
        if(!is_null($permissions)) {

            $user->givePermissionTo(array_values($permissions));
        }

        return $user;

    }

    public function show(int $user_id): User
    {
        $user = User::find($user_id);
        if(!$user) {
            throw new CustomException("Unknown User", Response::HTTP_BAD_REQUEST);
        }

        return $user;
    }

    public function update(array $data, int $user_id): User
    {
        $user = User::find($user_id);
        if(!$user) {
            throw new CustomException("Unknown User", Response::HTTP_BAD_REQUEST);
        }

        $user->first_name = $data['first_name'] ?? $user->first_name;
        $user->last_name = $data['last_name'] ?? $user->last_name;
        //$user->username = $data['username'] ?? $user->username;
        //$user->mobile_number = $data['mobile_number'] ?? $user->mobile_number;
        $user->img_url = $data['img_url'] ?? $user->img_url;
        $user->save();

        $user->roles()->detach();
        $user->assignRole($data['role']);

        $user->syncPermissions();
        $permissions =  data_get($data,'permissions');
        if(!is_null($permissions)) {

            $user->givePermissionTo(array_values($permissions));
        }

        return $user;
    }

    public function delete(int $user_id): bool
    {
        $user = User::find($user_id);
        if(!$user) {
            throw new CustomException("Unknown User", Response::HTTP_BAD_REQUEST);
        }

        return $user->delete();

    }


}